INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-1', 'Introducing Our New Sourdough Varieties', 'introducing-new-sourdough-varieties', '<p>We''re excited to announce three new sourdough varieties joining our lineup! After months of testing and perfecting our recipes, we''re proud to introduce Seeded Rye, Olive & Rosemary, and Sun-Dried Tomato & Basil sourdough loaves.</p><p>Each loaf is made with our signature 24-hour fermentation process and local ingredients...</p>', 'Three new artisan sourdough varieties are joining our regular lineup, including Seeded Rye, Olive & Rosemary, and Sun-Dried Tomato & Basil.', NULL, 'user-owner', 1, '2024-11-20T10:00:00.000Z', '2024-11-15T00:00:00.000Z', '2024-11-20T10:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-2', 'Christmas Bake Sale Dates Announced', 'christmas-bake-sale-dates-announced', '<p>The festive season is approaching, and we''re thrilled to announce our special Christmas bake sale schedule! We''ll be offering limited-edition seasonal treats including Stollen, Mince Pies, and Christmas Pudding.</p><p>Pre-orders open December 1st with collection dates on December 21st and 22nd...</p>', 'Mark your calendars! Our Christmas bake sale featuring Stollen, Mince Pies, and festive treats runs December 21-22. Pre-orders open December 1st.', NULL, 'user-manager', 1, '2024-11-15T10:00:00.000Z', '2024-11-10T00:00:00.000Z', '2024-11-15T10:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-3', 'Behind the Scenes: A Day in Our Bakery', 'behind-the-scenes-bakery-day', '<p>Ever wondered what goes into making your favourite artisan bread? Join us for a behind-the-scenes look at a typical baking day at Band of Bakers.</p><p>We start at 4 AM, shaping the dough that''s been fermenting overnight...</p>', 'Go behind the scenes and discover what it takes to create your favourite artisan bread. From 4 AM starts to the final bake.', NULL, 'user-owner', 1, '2024-11-10T10:00:00.000Z', '2024-11-05T00:00:00.000Z', '2024-11-10T10:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-4', 'Supporting Local Flour Mills', 'supporting-local-flour-mills', '<p>At Band of Bakers, we''re committed to using locally sourced ingredients. This month, we''re highlighting our partnership with Shipton Mill...</p>', 'Learn about our commitment to local sourcing and our partnership with Shipton Mill for premium British flour.', NULL, 'user-manager', 1, '2024-11-20T09:00:00.000Z', '2024-11-20T00:00:00.000Z', '2024-11-20T00:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-5', 'Welcoming Our New Head Pastry Chef', 'welcoming-our-new-head-pastry-chef', '<p>We are delighted to welcome Elena Rossi to the Band of Bakers family as our new Head Pastry Chef. Elena brings over 15 years of experience from bakeries in Paris and Rome.</p><p>Her signature croissants and danishes are already flying off the shelves. Come by this weekend to taste her creations!</p>', 'Meet Elena Rossi, our new Head Pastry Chef bringing 15 years of European baking experience to Shrewsbury.', NULL, 'user-owner', 1, '2024-10-15T09:00:00.000Z', '2024-10-10T00:00:00.000Z', '2024-10-15T00:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-6', 'Community Sourdough Workshop', 'community-sourdough-workshop', '<p>Due to popular demand, we''re hosting our first-ever Community Sourdough Workshop! Join us for a hands-on afternoon where you''ll learn the art of maintaining a starter, mixing, folding, and baking your own loaf.</p><p>Tickets are limited to 10 participants per session to ensure personal attention.</p>', 'Join us for a hands-on sourdough workshop. Learn to bake your own artisan bread from our expert bakers.', NULL, 'user-manager', 1, '2024-10-01T09:00:00.000Z', '2024-09-25T00:00:00.000Z', '2024-10-01T00:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-7', 'Finalist in National Bakery Awards', 'finalist-in-national-bakery-awards', '<p>We are incredibly honoured to be named a finalist in the ''Best Regional Bakery'' category at this year''s National Bakery Awards! This recognition means the world to our small team.</p><p>Thank you to all our wonderful customers for your continued support and for nominating us.</p>', 'Band of Bakers has been named a finalist for ''Best Regional Bakery'' in the National Bakery Awards!', NULL, 'user-owner', 1, '2024-09-10T09:00:00.000Z', '2024-09-05T00:00:00.000Z', '2024-09-10T00:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-8', 'Summer Menu Launch Party', 'summer-menu-launch-party', '<p>Summer is here, and so is our new menu! Think strawberry tarts, lemon drizzle cake, and focaccia with fresh garden herbs.</p><p>Join us this Saturday for our launch party with free tastings and live music from local artists.</p>', 'Celebrate the start of summer with our new seasonal menu launch party. Free tastings and live music!', NULL, 'user-manager', 1, '2024-06-01T09:00:00.000Z', '2024-05-25T00:00:00.000Z', '2024-06-01T00:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-9', 'Charity Bake Sale Success', 'charity-bake-sale-success', '<p>A huge thank you to everyone who came out to our charity bake sale last weekend. Together, we raised over ¬£1,500 for the Shrewsbury Food Bank!</p><p>It was heartwarming to see the community come together for such a great cause.</p>', 'We raised over ¬£1,500 for the Shrewsbury Food Bank! Thank you to everyone who supported our charity bake sale.', NULL, 'user-owner', 1, '2024-05-15T09:00:00.000Z', '2024-05-10T00:00:00.000Z', '2024-05-15T00:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-10', 'Why We Use Organic Butter', 'why-we-use-organic-butter', '<p>Butter is the soul of baking. That''s why we''ve made the switch to 100% organic butter from local dairy farms.</p><p>Not only does it taste richer and creamier, but it also supports sustainable farming practices. Taste the difference in our croissants!</p>', 'We''ve switched to 100% organic local butter. Discover why it makes our pastries taste even better.', NULL, 'user-manager', 1, '2024-04-20T09:00:00.000Z', '2024-04-15T00:00:00.000Z', '2024-04-20T00:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-11', 'Partnering with Bean & Brew', 'partnering-with-bean-and-brew', '<p>Great coffee deserves great pastries. We''re excited to announce our partnership with Bean & Brew Coffee House.</p><p>You can now find a selection of our fresh pastries and cakes at their High Street location every morning.</p>', 'Find our fresh pastries at Bean & Brew Coffee House! We''re partnering with local coffee experts to bring you the perfect breakfast.', NULL, 'user-owner', 1, '2024-03-10T09:00:00.000Z', '2024-03-05T00:00:00.000Z', '2024-03-10T00:00:00.000Z');
INSERT OR REPLACE INTO news_posts (id, title, slug, content, excerpt, image_url, author_id, is_published, published_at, created_at, updated_at) VALUES ('news-12', 'Expanding Our Shrewsbury Bakery', 'expanding-our-shrewsbury-bakery', '<p>We''re growing! Thanks to your support, we''re expanding our bakery to include a small seating area.</p><p>Soon you''ll be able to enjoy your coffee and cake right here with us. Renovations start next month!</p>', 'Big news! We''re expanding our bakery to include a seating area. Soon you can enjoy your treats in-store.', NULL, 'user-manager', 1, '2024-02-15T09:00:00.000Z', '2024-02-10T00:00:00.000Z', '2024-02-15T00:00:00.000Z');
INSERT OR REPLACE INTO email_templates (id, name, subject, content, variables, updated_at) VALUES ('tmpl_order_ready', 'order_ready_for_collection', 'Your Order is Ready for Collection! ü•ñ', '          <h1>Your Order is Ready!</h1>          <p>Hi {{customer_name}},</p>          <p>Great news! Your order #{{order_id}} is now ready for collection.</p>          <p><strong>Collection Details:</strong></p>          <ul>            <li>Location: {{location_name}}</li>            <li>Address: {{location_address}}</li>            <li>Time: {{collection_time}}</li>          </ul>          <p>Please bring your order number with you.</p>          <p>See you soon!</p>          <p>Band of Bakers</p>        ', '["customer_name","order_id","location_name","location_address","collection_time"]', CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO email_templates (id, name, subject, content, variables, updated_at) VALUES ('tmpl_order_completed', 'order_completed', 'Thank you for your order! üåü', '          <h1>Thank You!</h1>          <p>Hi {{customer_name}},</p>          <p>Thanks for collecting your order #{{order_id}}. We hope you enjoy your bakes!</p>          <p>If you have a moment, we''d love to hear your feedback.</p>          <p>Best regards,</p>          <p>Band of Bakers</p>        ', '["customer_name","order_id"]', CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO email_templates (id, name, subject, content, variables, updated_at) VALUES ('tmpl_bake_sale_cancelled', 'bake_sale_cancelled', 'Important: Bake Sale Cancelled ‚ö†Ô∏è', '          <h1>Bake Sale Update</h1>          <p>Hi {{customer_name}},</p>          <p>We regret to inform you that the bake sale scheduled for {{date}} at {{location_name}} has been cancelled.</p>          <p><strong>Reason:</strong> {{reason}}</p>          <p>Your order #{{order_id}} has been cancelled and a full refund has been processed.</p>          <p>We apologize for any inconvenience caused.</p>          <p>Band of Bakers</p>        ', '["customer_name","date","location_name","reason","order_id"]', CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO email_templates (id, name, subject, content, variables, updated_at) VALUES ('tmpl_bake_sale_rescheduled', 'bake_sale_rescheduled', 'Action Required: Bake Sale Rescheduled üìÖ', '          <h1>Bake Sale Rescheduled</h1>          <p>Hi {{customer_name}},</p>          <p>The bake sale scheduled for {{old_date}} has been moved to <strong>{{new_date}}</strong>.</p>          <p><strong>Reason:</strong> {{reason}}</p>          <p>We have updated your order #{{order_id}} to the new date. If this doesn''t work for you, please contact us to cancel for a full refund.</p>          <p>Band of Bakers</p>        ', '["customer_name","old_date","new_date","reason","order_id"]', CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO email_templates (id, name, subject, content, variables, updated_at) VALUES ('tmpl_action_required', 'action_required', 'Action Required: Update on your Order ‚ö†Ô∏è', '          <h1>Action Required</h1>          <p>Hi {{customer_name}},</p>          <p>There has been a change to the bake sale scheduled for {{date}}.</p>          <p><strong>Please review your options:</strong></p>          <p><a href="{{resolution_link}}">Click here to view options</a></p>          <p>You can choose to transfer your order to another date or cancel for a full refund.</p>          <p>Band of Bakers</p>        ', '["customer_name","date","resolution_link"]', CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO email_templates (id, name, subject, content, variables, updated_at) VALUES ('tmpl_order_update_bakery', 'order_update_bakery', 'Important Update to Your Order #{{order_id}} ‚ö†Ô∏è', '          <h1>Order Update Required</h1>          <p>Hi {{customer_name}},</p>          <p>We need to inform you of some changes to your order #{{order_id}}.</p>          <p><strong>Changes:</strong></p>          {{change_details}}          <p><strong>Updated Total:</strong> ¬£{{new_total}}</p>          <p>We apologize for any inconvenience this may cause. If you have any questions or concerns, please don''t hesitate to contact us.</p>          <p>Best regards,<br>Band of Bakers</p>        ', '["customer_name","order_id","change_details","new_total"]', CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO email_templates (id, name, subject, content, variables, updated_at) VALUES ('tmpl_order_update_customer', 'order_update_customer', 'Confirmation: Your Order Changes #{{order_id}} ‚úì', '          <h1>Order Changes Confirmed</h1>          <p>Hi {{customer_name}},</p>          <p>As requested, we''ve made the following changes to your order #{{order_id}}:</p>          <p><strong>Changes:</strong></p>          {{change_details}}          <p><strong>Updated Total:</strong> ¬£{{new_total}}</p>          <p>Thank you for letting us know. If you need any further changes, please contact us.</p>          <p>Best regards,<br>Band of Bakers</p>        ', '["customer_name","order_id","change_details","new_total"]', CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-1', 'How do I place an order?', 'Browse our menu, select your items, choose a bake sale date, and complete checkout. You must order before the cutoff date (typically 2 days before the bake sale).', 'ordering', 1, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-2', 'What is the order cutoff time?', 'Orders must be placed at least 24 hours before the bake sale date, typically by 11:00 AM. Check each bake sale listing for specific cutoff times.', 'ordering', 2, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-3', 'Can I modify or cancel my order?', 'Yes, you can modify or cancel your order up until the cutoff time. After that, we''ve already started baking and changes cannot be made.', 'ordering', 3, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-4', 'Do you accept payment on collection?', 'Yes! We accept both online payment (Stripe) and payment on collection. If paying on collection, we accept cash and card.', 'ordering', 4, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-5', 'Do you offer delivery?', 'Yes, we offer delivery to selected areas in Shropshire for a small fee. Delivery areas include Cressage, Shrewsbury, and Telford. Check your postcode at checkout.', 'delivery', 1, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-6', 'What are the collection times?', 'Collection times vary by location and bake sale. Typical hours are 10:00 AM - 4:00 PM. You''ll receive specific collection details in your order confirmation.', 'delivery', 2, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-7', 'What if I can''t collect at the specified time?', 'Please contact us as soon as possible if you need to arrange an alternative collection time. We''ll do our best to accommodate you within the same day.', 'delivery', 3, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-8', 'Are your products suitable for vegetarians?', 'Most of our products are vegetarian. We use local dairy and eggs. Check individual product descriptions for specific dietary information.', 'products', 1, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-9', 'Do you cater for allergies and dietary requirements?', 'While we list common allergens on our products, all items are made in a kitchen that handles nuts, gluten, dairy, and eggs. Please contact us for specific allergy concerns.', 'products', 2, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-10', 'How long do your baked goods stay fresh?', 'Our bread is best within 2-3 days and can be frozen for up to 3 months. Pastries are best enjoyed on the day of collection. Cakes will last 3-5 days when stored properly.', 'products', 3, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-11', 'What makes your sourdough special?', 'Our sourdough is made with a natural starter that''s been cultivated for years. We use long fermentation (up to 24 hours) for better flavor, texture, and digestibility.', 'products', 4, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-12', 'How often do you have bake sales?', 'We typically hold bake sales weekly or fortnightly at various locations around Shropshire. Check our menu or follow us on social media for the latest schedule.', 'general', 1, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-13', 'Can I place a custom order?', 'Yes! We accept custom orders for special occasions. Please contact us at least 2 weeks in advance to discuss your requirements and pricing.', 'general', 2, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-14', 'Do you offer wholesale or supply to cafes/restaurants?', 'We''re currently focused on our bake sale model for individual customers, but we''re happy to discuss wholesale opportunities. Please get in touch to explore options.', 'general', 3, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
INSERT OR REPLACE INTO faqs (id, question, answer, category, sort_order, is_active, created_at, updated_at) VALUES ('faq-15', 'How can I stay updated on new products and bake sales?', 'Follow us on Instagram, Facebook, and Twitter for the latest updates, special bakes, and bake sale announcements. You can also subscribe to our newsletter (coming soon!).', 'general', 4, 1, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);