INSERT OR REPLACE INTO testimonials (id, name, role, content, rating, avatar_url, user_id, status, created_at, updated_at) VALUES ('rtest-1', 'Sarah Mitchell', 'Regular Customer', 'Band of Bakers has become a weekly treat for our family! The sourdough is absolutely incredible - crusty on the outside, perfectly soft inside. We''ve tried everything from their foccacia to the Portuguese custard tarts, and every single item has been outstanding.', 5, NULL, 'user-cust-1', 'approved', '2024-09-15T00:00:00.000Z', '2024-09-15T00:00:00.000Z');
INSERT OR REPLACE INTO testimonials (id, name, role, content, rating, avatar_url, user_id, status, created_at, updated_at) VALUES ('rtest-2', 'James Thompson', 'Local Business Owner', 'We order from Band of Bakers for all our office meetings. The croissants and pastries are always fresh, and the service is excellent. The lemon drizzle cake is particularly popular with our team!', 5, NULL, 'user-cust-2', 'approved', '2024-10-02T00:00:00.000Z', '2024-10-02T00:00:00.000Z');
INSERT OR REPLACE INTO testimonials (id, name, role, content, rating, avatar_url, user_id, status, created_at, updated_at) VALUES ('rtest-3', 'Emma Davies', 'Food Blogger', 'As someone who reviews food regularly, I can honestly say Band of Bakers produces some of the finest baked goods I''ve encountered. Their flapjacks are legendary, and the apple pies - both small and large - are perfection!', 5, NULL, 'user-cust-3', 'approved', '2024-08-20T00:00:00.000Z', '2024-08-20T00:00:00.000Z');
INSERT OR REPLACE INTO testimonials (id, name, role, content, rating, avatar_url, user_id, status, created_at, updated_at) VALUES ('rtest-4', 'Michael O''Connor', 'Weekly Subscriber', 'I''ve been ordering weekly for over six months now. The quality never wavers - everything is consistently delicious. Special mention to the curried beef pasties and the eccles cakes. Absolute gems!', 5, NULL, 'user-cust-4', 'approved', '2024-07-10T00:00:00.000Z', '2024-07-10T00:00:00.000Z');
INSERT OR REPLACE INTO testimonials (id, name, role, content, rating, avatar_url, user_id, status, created_at, updated_at) VALUES ('rtest-5', 'Lisa Patel', 'Loyal Customer', 'The cinnamon knots are to die for! And don''t even get me started on the millionaire shortbread - it''s dangerously addictive. Love supporting a local business that clearly takes pride in their craft.', 5, NULL, 'user-cust-5', 'approved', '2024-11-05T00:00:00.000Z', '2024-11-05T00:00:00.000Z');
INSERT OR REPLACE INTO testimonials (id, name, role, content, rating, avatar_url, user_id, status, created_at, updated_at) VALUES ('rtest-6', 'Robert Hughes', 'Customer Since 2024', 'Discovered Band of Bakers at a local market and now we''re hooked! The wholemeal loaf makes the best toast, and the kids love the Portuguese custard tarts. Can''t recommend enough!', 5, NULL, 'user-cust-6', 'approved', '2024-06-18T00:00:00.000Z', '2024-06-18T00:00:00.000Z');
INSERT OR REPLACE INTO testimonials (id, name, role, content, rating, avatar_url, user_id, status, created_at, updated_at) VALUES ('rtest-7', 'Catherine Williams', 'Happy Customer', 'Every single product I''ve tried has been exceptional. The attention to detail and quality ingredients really show. The lemon meringue pie was the star of our dinner party!', 5, NULL, 'user-cust-7', 'approved', '2024-10-22T00:00:00.000Z', '2024-10-22T00:00:00.000Z');
INSERT OR REPLACE INTO testimonials (id, name, role, content, rating, avatar_url, user_id, status, created_at, updated_at) VALUES ('rtest-8', 'David Chen', 'Regular Buyer', 'Fantastic bakery! The foccacia is restaurant-quality, and I''m obsessed with the peach pastries. Perfect for weekend brunches. Always arrives fresh and beautifully presented.', 5, NULL, 'user-cust-8', 'approved', '2024-09-30T00:00:00.000Z', '2024-09-30T00:00:00.000Z');
INSERT OR REPLACE INTO testimonials (id, name, role, content, rating, avatar_url, user_id, status, created_at, updated_at) VALUES ('rtest-9', 'Rachel Green', 'Monthly Subscriber', 'The whole tarte au citron was the highlight of my birthday celebration. Absolutely stunning and tasted incredible. Band of Bakers never disappoints!', 5, NULL, 'user-cust-9', 'approved', '2024-08-12T00:00:00.000Z', '2024-08-12T00:00:00.000Z');
